import React, { useEffect, useState } from 'react';
import { getAllDisciplines, createDiscipline } from './services/DisciplineService';
import DisciplineForm from './components/DisciplineForm';

const App = () => {
  const [disciplines, setDisciplines] = useState([]);

  useEffect(() => {
    fetchDisciplines();
  }, []);

  const fetchDisciplines = async () => {
    try {
      const response = await getAllDisciplines();
      setDisciplines(response.data.disciplines);
    } catch (error) {
      if (error.response) {
          alert(error.response.data.message); // Exibe no navegador
      } else {
          console.error("Erro de rede ou configuração:", error.message);
      }
    }
  };

  const handleCreate = async (formData) => {
    try {
      await createDiscipline(formData);
      fetchDisciplines();
    } catch (error) {
      if (error.response) {
        // Acesse a mensagem do backend
        console.error(error.response.data.message); // Mostra no console
        alert(error.response.data.message); // Exibe no navegador
      } else {
          console.error("Erro de rede ou configuração:", error.message);
      }
    }
  };

  return (
    <div>
      <h1>Discipline Manager</h1>
      <DisciplineForm onSubmit={handleCreate} />
      <ul>
        {disciplines.map((discipline) => (
          <li key={discipline.id}>{discipline.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;

